+/* General styles for the header */
+.header {
+  width: 100%;
+  max-width: 1280px;
+  height: auto;
+  padding: 42px 24px;
+  display: flex;
+  justify-content: flex-start;
+  gap: 68px;
+  background-color: #FFFFFF;
+  align-items: center; 
+}
+
+.header-content {
+  display: flex;
+  flex-direction: column;
+  align-items: flex-start; /* memastikan konten rata kiri */
+  padding: 0;
+  gap: 48px;
+  width: 620px;
+  min-width: 320px;
+  max-width: 620px;
+  height: 328px;
+}
+
+/* Title and Subtitle styling */
+.title-subtitle {
+  width: 100%; /* Sesuaikan dengan konten penuh */
+  font-family: 'Epilogue', sans-serif;
+  font-style: normal;
+  font-weight: 600;
+  font-size: 20px;
+  line-height: 30px;
+  color: #2D2D2D;
+  margin-left: 0; /* hapus margin jika ingin rata kiri */
+}
+
+/* Visual Designer styling */
+.visual-designer {
+  width: 100%; /* Sesuaikan dengan konten penuh */
+  font-family: 'Epilogue', sans-serif;
+  font-style: normal;
+  font-weight: 600;
+  font-size: 68px;
+  line-height: 70px;
+  color: #2D2D2D;
+  margin-left: 0; /* hapus margin jika ingin rata kiri */
+}
+
+/* Additional Content styling */
+.additional-content {
+  width: 100%; /* Sesuaikan dengan konten penuh */
+  font-family: 'Epilogue', sans-serif;
+  font-style: normal;
+  font-weight: 400;
+  font-size: 17px;
+  line-height: 27px;
+  color: #2D2D2D;
+  margin-left: 0; /* hapus margin jika ingin rata kiri */
+}
+
+/* Button styling */
+.header-button {
+  display: flex;
+  flex-direction: row;
+  justify-content: center;
+  align-items: center;
+  padding: 24px 64px;
+  gap: 10px;
+  width: 211px;
+  height: 78px;
+  background-color: #2D2D2D;
+  color: #FFFFFF;
+  border: none;
+  border-radius: 4px;
+  font-family: 'Epilogue', sans-serif;
+  font-size: 20px;
+  font-weight: 600;
+  text-align: center;
+  cursor: pointer;
+}
+
+/* Image styling */
+.header-image {
+  width: 480px;
+  height: 360px;
+}
+
+.header-image img {
+  width: 100%;
+  height: 100%;
+  object-fit: cover;
+}

