@@ -0,0 +1,96 @@
+/* Styling untuk Testimonial Section */
+.testimoni {
+    padding: 42px 24px;
+    background: #FFFFFF;
+  }
+  
+  .testimoni-title {
+    font-family: 'Epilogue', sans-serif;
+    font-size: 32px;
+    font-weight: 700;
+    color: #2D2D2D;
+    text-align: center;
+    margin-bottom: 42px;
+  }
+  
+  .testimoni-content {
+    display: flex;
+    flex-wrap: wrap;
+    gap: 24px;
+    justify-content: space-between;
+  }
+  
+  .testimoni-item {
+    background: #F3F3F3;
+    width: calc(33.333% - 16px); /* 3 kolom dengan gap */
+    padding: 42px 0 0 0;
+    box-sizing: border-box;
+    display: flex;
+    flex-direction: column;
+    gap: 68px;
+    opacity: 0;
+  }
+  
+  .testimoni-text p {
+    font-family: 'Epilogue', sans-serif;
+    font-size: 20px;
+    font-weight: 600;
+    line-height: 30px;
+    color: #2D2D2D;
+    padding: 0 24px;
+  }
+  
+  .testimoni-footer {
+    display: flex;
+    align-items: center;
+    gap: 16px;
+    padding: 0 24px 42px;
+  }
+  
+  .testimoni-avatar {
+    width: 50px;
+    height: 50px;
+    border-radius: 50%;
+  }
+  
+  .testimoni-keterangan {
+    display: flex;
+    flex-direction: column;
+  }
+  
+  .testimoni-stars {
+    display: flex;
+    gap: 6px;
+  }
+  
+  .testimoni-star {
+    width: 20px;
+    height: 20px;
+  }
+  
+  .testimoni-nama-company .nama {
+    font-family: 'Epilogue', sans-serif;
+    font-size: 20px;
+    font-weight: 600;
+    color: #2D2D2D;
+  }
+  
+  .testimoni-nama-company .company {
+    font-family: 'Epilogue', sans-serif;
+    font-size: 17px;
+    font-weight: 400;
+    color: #2D2D2D;
+  }
+  
+  @media screen and (max-width: 1024px) {
+    .testimoni-item {
+      width: calc(50% - 16px); /* 2 kolom pada layar lebih kecil */
+    }
+  }
+  
+  @media screen and (max-width: 768px) {
+    .testimoni-item {
+      width: 100%; /* 1 kolom pada layar kecil */
+    }
+  }
+  
